/* eslint-disable jsx-a11y/anchor-is-valid */
import { useEffect, useState } from "react";
import * as BooksAPI from "./BooksAPI";
import Bookshelf from "./Bookshelf";
import { Link } from "react-router-dom";

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    };

    getAllBooks();
  }, []);

  const GetBooks = (shelf) => books.filter(b => b.shelf === shelf);

  return (
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      <div className="list-books-content">
          <div>
            <Bookshelf title="Currently Reading" books={GetBooks("currentlyReading")} />
            <Bookshelf title="Want to Read" books={GetBooks("wantToRead")} />
            <Bookshelf title="Read" books={GetBooks("read")} />
        </div>
      </div>
      <div className="open-search">
        <Link to="/search">Add a book</Link>
        <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
      </div>
    </div>
  );
}

export default BookList;
