import BookCover from "./BookCover";
import BookshelfChanger from "./BookshelfChanger";

const Book = ({ book }) => {
    if (!book) return null;

    const { title, authors, imageLinks } = book;
    const imageUrl = (imageLinks && (imageLinks.smallThumbnail || imageLinks.thumbnail));
    const backgroundImage = imageUrl && `url("${imageUrl}")` || 'none';

    console.log({ title, authors, imageLinks, imageUrl });

    return (
        <div className="book">
            <div className="book-top">
                <BookCover imageLinks={book.imageLinks} />
                <BookshelfChanger/>
                <div className="book-shelf-changer">
                    <select>
                        <option value="none" disabled>
                            Move to...
                        </option>
                        <option value="currentlyReading">
                            Currently Reading
                        </option>
                        <option value="wantToRead">Want to Read</option>
                        <option value="read">Read</option>
                        <option value="none">None</option>
                    </select>
                </div>
            </div>
            <div className="book-title">{title}</div>
            <div className="book-authors">{authors}</div>
            <div className="book-authors">{backgroundImage}</div>
        </div>);
}

export default Book;
