/* eslint-disable jsx-a11y/anchor-is-valid */
import "./App.css";
import { useEffect, useState } from "react";
import Book from "./Book";
import * as BooksAPI from "./BooksAPI";
import Bookshelf from "./Bookshelf";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    };

    getAllBooks();
  }, []);

  const GetBooks = (shelf) => books.filter(b => b.shelf === shelf);

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a href="#"
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
              <div>
                <Bookshelf title="Currently Reading" books={GetBooks("currentlyReading")} />
                <Bookshelf title="Want to Read" books={GetBooks("wantToRead")} />
                <Bookshelf title="Read" books={GetBooks("read")} />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
