import "./App.css";
import BookList from "./BookList";
import BookSearch from "./BookSearch";
import { Route, Routes } from "react-router-dom";
import { useEffect, useState } from "react";
import * as BooksAPI from "./BooksAPI";

const maxSearchResults = 100;

function App() {
  const [books, setBooks] = useState([]);

  const refreshData = async () => {
    const res = await BooksAPI.getAll();  
    console.debug("Got books", {count: Object.keys(res).length});
      setBooks(res);
  };

  useEffect(() => {
    refreshData();
  }, []);

  const setShelf = (book, shelf) => {
    console.debug('Setting book shelf', { book, shelf });
    const update = async () => {
      await BooksAPI.update(book, shelf);

      refreshData();
    };

    update();
  };
  return (

    <Routes>
      <Route exact path="/" element={<BookList books={books} setShelf={setShelf}/>} />
      <Route exact path="/search" element={<BookSearch books={books} setShelf={setShelf} />} />
    </Routes>
   );
}

export default App;
